mapper.select:
select 'in' as action,ID,CPH as plateNo,InTime as time from dbo.MYCARCOMERECORD where InTime between #{timeFrom} and #{timeTo}
union all
select 'out' as action,ID,CPH as plateNo,OutTime as time from dbo.MYCARGOOUTRECORD where OutTime between #{timeFrom} and #{timeTo}

select '0' as inouttype,#{devNo} as devno,ID,CPH as plateno,InTime as inoutdtime from dbo.MYCARCOMERECORD


        InTime >= #{timeFrom} and InTime <= #{timeTo}


        and ID > #{recordId}

select '1' as inouttype,#{devNo} as devno,ID,CPH as plateno,OutTime as inoutdtime from dbo.MYCARGOOUTRECORD


        OutTime between #{timeFrom} and #{timeTo}


        and ID > #{recordId}
